PRAGMA foreign_keys=OFF;
BEGIN TRANSACTION;
CREATE TABLE sources (
    source_id TEXT PRIMARY KEY,
    name TEXT NOT NULL,
    url TEXT NOT NULL,
    type TEXT,
    has_rss INTEGER DEFAULT 0,
    last_status TEXT,
    last_error TEXT,
    success_rate REAL DEFAULT 0.0,
    last_parsed DATETIME,
    total_articles INTEGER DEFAULT 0,
    selectors TEXT,
    category TEXT,
    validation_status TEXT,
    circuit_breaker_failures INTEGER DEFAULT 0,
    circuit_breaker_reset_time TIMESTAMP
, last_article_discovery DATETIME, consecutive_failures INTEGER DEFAULT 0, rss_url TEXT, last_rss_check DATETIME, rss_fetch_frequency INTEGER DEFAULT 3600);
CREATE TABLE articles (
    article_id TEXT PRIMARY KEY,
    source_id TEXT NOT NULL,
    url TEXT NOT NULL UNIQUE,
    title TEXT NOT NULL,
    description TEXT,
    published_date DATETIME,
    content TEXT,
    content_status TEXT DEFAULT 'pending',
    content_error TEXT,
    parsed_at DATETIME,
    media_count INTEGER DEFAULT 0,
    created_at DATETIME DEFAULT CURRENT_TIMESTAMP, crawl_job_id TEXT, discovered_via TEXT DEFAULT 'crawl', retry_count INTEGER DEFAULT 0, rss_guid TEXT, tags TEXT, word_count INTEGER, reading_time_minutes INTEGER, summary TEXT, categories TEXT, language TEXT,
    FOREIGN KEY (source_id) REFERENCES sources(source_id)
);
ANALYZE sqlite_schema;
CREATE TABLE related_links (
                id INTEGER PRIMARY KEY AUTOINCREMENT,
                article_id TEXT NOT NULL,
                title TEXT NOT NULL,
                url TEXT NOT NULL,
                created_at DATETIME DEFAULT CURRENT_TIMESTAMP,
                FOREIGN KEY (article_id) REFERENCES articles(article_id)
            );
CREATE TABLE wordpress_articles (
    id INTEGER PRIMARY KEY AUTOINCREMENT,
    article_id TEXT NOT NULL,
    
    -- WordPress Content
    title TEXT NOT NULL,
    content TEXT NOT NULL,
    excerpt TEXT,
    slug TEXT NOT NULL,
    
    -- WordPress Meta
    categories TEXT,  -- JSON array
    tags TEXT,        -- JSON array
    
    -- Yoast SEO (correct field names)
    _yoast_wpseo_title TEXT,
    _yoast_wpseo_metadesc TEXT,
    focus_keyword TEXT,
    
    -- Media
    featured_image_index INTEGER,
    images_data TEXT,  -- JSON with all image metadata
    
    -- Status
    translation_status TEXT DEFAULT 'pending',
    translation_error TEXT,
    translated_at DATETIME,
    published_to_wp BOOLEAN DEFAULT 0,
    wp_post_id INTEGER,
    
    -- Metadata
    source_language TEXT,
    target_language TEXT DEFAULT 'ru',
    llm_model TEXT,
    created_at DATETIME DEFAULT CURRENT_TIMESTAMP, updated_at DATETIME,
    
    FOREIGN KEY (article_id) REFERENCES articles(article_id)
);
CREATE TABLE global_config (
                    key TEXT PRIMARY KEY,
                    value TEXT NOT NULL,
                    description TEXT,
                    updated_at DATETIME DEFAULT CURRENT_TIMESTAMP
                );
CREATE TABLE IF NOT EXISTS "media_files" (
    id INTEGER PRIMARY KEY AUTOINCREMENT,
    article_id TEXT NOT NULL,
    url TEXT NOT NULL,
    type TEXT,
    file_path TEXT,
    created_at DATETIME DEFAULT CURRENT_TIMESTAMP,
    media_id TEXT,
    source_id TEXT,
    file_size INTEGER,
    mime_type TEXT,
    width INTEGER,
    height INTEGER,
    alt_text TEXT,
    status TEXT DEFAULT 'pending',
    error TEXT,
    source TEXT,
    caption TEXT, wp_media_id INTEGER, wp_upload_status TEXT DEFAULT 'pending', wp_uploaded_at DATETIME, alt_text_ru TEXT, caption_ru TEXT,
    FOREIGN KEY (article_id) REFERENCES articles(article_id)
);
DELETE FROM sqlite_sequence;
CREATE INDEX idx_sources_discovery ON sources(last_article_discovery DESC);
CREATE INDEX idx_articles_rss_workflow ON articles(source_id, published_date DESC, content_status);
CREATE INDEX idx_articles_status_date ON articles(content_status, parsed_at DESC) WHERE content_status IN ('pending', 'parsing');
CREATE INDEX idx_articles_url_unique ON articles(url);
CREATE INDEX idx_sources_rss ON sources(has_rss, last_rss_check);
CREATE INDEX idx_articles_recent ON articles(created_at DESC) WHERE content_status = 'completed';
CREATE INDEX idx_articles_rss_guid ON articles(rss_guid) WHERE rss_guid IS NOT NULL;
CREATE INDEX idx_articles_created_at ON articles(created_at DESC);
CREATE INDEX idx_articles_published_date ON articles(published_date DESC);
CREATE INDEX idx_related_links_article_id ON related_links(article_id);
CREATE INDEX idx_articles_source_id ON articles(source_id);
CREATE INDEX idx_articles_status ON articles(content_status);
CREATE INDEX idx_articles_created ON articles(created_at);
CREATE INDEX idx_media_article_id ON media_files(article_id);
CREATE INDEX idx_media_status ON media_files(status);
CREATE INDEX idx_media_wp_status ON media_files(wp_upload_status) WHERE wp_upload_status IS NOT NULL;
CREATE INDEX idx_media_wp_id ON media_files(wp_media_id) WHERE wp_media_id IS NOT NULL;
COMMIT;
